<script>
export default {
  name: 'ServicesIcon',
  props: {
    type: {
      type: String,
      required: true,
      description: 'The service icon (values: all|auth|cms|db|log|memory|messaging|monitoring|optimization|popular|search|worker)',
      validator: v => ['all', 'auth', 'cms', 'db', 'log', 'memory', 'messaging', 'monitoring', 'optimization', 'popular', 'search', 'worker'].includes(v)
    },
    active: {
      type: Boolean,
      default: false,
      description: 'Active icon (primary color)'
    },
    light: {
      type: Boolean,
      default: false,
      description: 'Light color icon'
    }
  },
  data: () => ({
    data: {
      all: { width: 30, height: 20 },
      auth: { width: 25, height: 25 },
      cms: { width: 25.18, height: 25.18 },
      db: { width: 24.5, height: 24.5 },
      log: { width: 23.84, height: 22.97 },
      memory: { width: 25, height: 23 },
      messaging: { width: 24.5, height: 25.1 },
      monitoring: { width: 25, height: 24.6 },
      optimization: { width: 25, height: 25 },
      popular: { width: 25, height: 25 },
      search: { width: 24.2, height: 24.2 },
      worker: { width: 25, height: 25 }
    }
  }),
  computed: {
    viewBox: function () {
      return `0 0 ${this.width} ${this.height}`
    },
    width: function () {
      return this.data[this.type].width
    },
    height: function () {
      return this.data[this.type].height
    }
  }
}
</script>

